@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 220 20% 6%;
    --foreground: 0 0% 98%;

    --card: 220 20% 10%;
    --card-foreground: 0 0% 98%;

    --popover: 220 20% 10%;
    --popover-foreground: 0 0% 98%;

    --primary: 280 85% 65%;
    --primary-foreground: 0 0% 100%;

    --secondary: 220 20% 15%;
    --secondary-foreground: 0 0% 98%;

    --muted: 220 15% 20%;
    --muted-foreground: 220 10% 60%;

    --accent: 320 80% 60%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;

    --border: 220 15% 18%;
    --input: 220 15% 18%;
    --ring: 280 85% 65%;

    --radius: 1rem;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(280 85% 65%), hsl(320 80% 60%));
    --gradient-glow: linear-gradient(135deg, hsl(280 85% 65% / 0.5), hsl(320 80% 60% / 0.5));
    --gradient-card: linear-gradient(145deg, hsl(220 20% 12%), hsl(220 20% 8%));
    --gradient-text: linear-gradient(135deg, hsl(280 85% 70%), hsl(320 80% 65%));

    /* Custom shadows */
    --shadow-glow: 0 0 30px hsl(280 85% 65% / 0.3);
    --shadow-glow-lg: 0 0 60px hsl(280 85% 65% / 0.4);
    --shadow-card: 0 8px 32px hsl(0 0% 0% / 0.3);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer utilities {
  .glass {
    @apply bg-card/60 backdrop-blur-xl border border-border/50;
  }

  .glass-hover {
    @apply transition-all duration-300 hover:bg-card/80 hover:border-border;
  }

  .gradient-text {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-text);
  }

  .gradient-border {
    position: relative;
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 1px;
    border-radius: inherit;
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .upload-zone {
    @apply relative overflow-hidden rounded-2xl border-2 border-dashed border-border/50
           bg-card/30 backdrop-blur-sm transition-all duration-300
           hover:border-primary/50 hover:bg-card/50;
  }

  .upload-zone.active {
    @apply border-primary bg-primary/10;
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--muted)) 0%,
      hsl(var(--card)) 50%,
      hsl(var(--muted)) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  @keyframes shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 20px hsl(280 85% 65% / 0.3); }
    50% { box-shadow: 0 0 40px hsl(280 85% 65% / 0.5); }
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }
}

