    id: '4',
    name: '牛仔外套',
    image: 'https://images.unsplash.com/photo-1551028719-00167b16eac5?w=400&h=500&fit=crop',
    category: '外套'
  },
  {
    id: '5',
    name: '红色毛衣',
    image: 'https://images.unsplash.com/photo-1434389677669-e08b4cac3105?w=400&h=500&fit=crop',
    category: '上衣'
  },
  {
    id: '6',
    name: '格子衬衫',
    image: 'https://images.unsplash.com/photo-1596755094514-f87e34085b2c?w=400&h=500&fit=crop',
    category: '上衣'
  }
];

interface ClothingGalleryProps {
  selectedId: string | null;
  onSelect: (item: ClothingItem) => void;
}

export function ClothingGallery({ selectedId, onSelect }: ClothingGalleryProps) {
  return (
    <div className="w-full">
      <h3 className="text-lg font-medium text-foreground mb-4">或从服装库选择</h3>
      <div className="grid grid-cols-3 gap-3">
        {clothingItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onSelect(item)}
            className={`relative aspect-[3/4] rounded-xl overflow-hidden transition-all duration-300
                       border-2 ${
                         selectedId === item.id
                           ? 'border-primary shadow-glow scale-[1.02]'
                           : 'border-transparent hover:border-primary/50'
                       }`}
          >
            <img
              src={item.image}
              alt={item.name}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-background/80 to-transparent" />
            <div className="absolute bottom-0 left-0 right-0 p-2">
              <p className="text-foreground text-xs font-medium truncate">{item.name}</p>
              <p className="text-muted-foreground text-[10px]">{item.category}</p>
            </div>
            {selectedId === item.id && (
              <div className="absolute top-2 right-2 w-6 h-6 rounded-full bg-primary flex items-center justify-center">
                <Check className="w-4 h-4 text-primary-foreground" />
              </div>
            )}
          </button>
        ))}
      </div>
    </div>
  );
}

export type { ClothingItem };

import { Download, RefreshCw, Sparkles } from 'lucide-react';

interface ResultDisplayProps {
  isProcessing: boolean;
  resultImage: string | null;
  onReset: () => void;
}

export function ResultDisplay({ isProcessing, resultImage, onReset }: ResultDisplayProps) {
  const handleDownload = () => {
    if (resultImage) {
      const link = document.createElement('a');
      link.href = resultImage;
      link.download = 'ai-outfit-result.png';
      link.click();
    }
  };

  return (
    <div className="w-full">
      <h3 className="text-lg font-medium text-foreground mb-4 flex items-center gap-2">
        <Sparkles className="w-5 h-5 text-accent" />
        换装效果
      </h3>

      <div className="relative aspect-[3/4] rounded-2xl overflow-hidden glass">
        {isProcessing ? (
          <div className="absolute inset-0 flex flex-col items-center justify-center gap-4">
            <div className="relative w-20 h-20">
              <div className="absolute inset-0 rounded-full border-4 border-primary/20" />
              <div className="absolute inset-0 rounded-full border-4 border-primary border-t-transparent animate-spin" />
              <Sparkles className="absolute inset-0 m-auto w-8 h-8 text-primary animate-pulse" />
            </div>
            <div className="text-center">
              <p className="text-foreground font-medium">AI 正在处理中...</p>
              <p className="text-muted-foreground text-sm mt-1">请稍候，这可能需要几秒钟</p>
            </div>
          </div>
        ) : resultImage ? (
          <>
            <img
              src={resultImage}
              alt="Result"
              className="w-full h-full object-cover"
            />
            <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-background/90 to-transparent">
              <div className="flex gap-2">
                <button
                  onClick={handleDownload}
                  className="flex-1 flex items-center justify-center gap-2 py-3 rounded-xl
                           bg-primary text-primary-foreground font-medium
                           hover:opacity-90 transition-opacity"
                >
                  <Download className="w-4 h-4" />
                  下载图片
                </button>
                <button
                  onClick={onReset}
                  className="p-3 rounded-xl bg-secondary text-secondary-foreground
                           hover:bg-secondary/80 transition-colors"
                >
                  <RefreshCw className="w-5 h-5" />
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="absolute inset-0 flex flex-col items-center justify-center gap-4 p-8">
            <div className="w-20 h-20 rounded-full bg-muted/50 flex items-center justify-center">
              <Sparkles className="w-10 h-10 text-muted-foreground" />
            </div>
            <div className="text-center">
              <p className="text-muted-foreground">上传人物和服装图片</p>
              <p className="text-muted-foreground text-sm mt-1">点击生成按钮查看效果</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import { Wand2 } from 'lucide-react';

interface GenerateButtonProps {
  onClick: () => void;
  disabled: boolean;
  isProcessing: boolean;
}

export function GenerateButton({ onClick, disabled, isProcessing }: GenerateButtonProps) {
  return (
    <button
      onClick={onClick}
      disabled={disabled || isProcessing}
      className={`w-full py-4 px-8 rounded-2xl font-semibold text-lg
                 flex items-center justify-center gap-3 transition-all duration-300
                 ${
                   disabled || isProcessing
                     ? 'bg-muted text-muted-foreground cursor-not-allowed'
                     : 'bg-gradient-primary text-primary-foreground shadow-glow hover:shadow-glow-lg hover:scale-[1.02] active:scale-[0.98]'
                 }`}
      style={{
        background: disabled || isProcessing ? undefined : 'var(--gradient-primary)'
      }}
    >
      <Wand2 className={`w-6 h-6 ${isProcessing ? 'animate-spin' : ''}`} />
      {isProcessing ? 'AI 生成中...' : '开始 AI 换装'}
    </button>
  );
}

